class Solution {
public:
    ll dp[101][4][102][102];
    ll solve(vector<vpi> &vec,int idx,int cnt,int p1,int p2)
    {
        if(cnt==3) return 0;
        if(idx>=vec.size()) return -1e15;
        ll &t = dp[idx][cnt][p1+1][p2+1];
        if(t!=-1) return t;
        ll ans = -1e15;
        fori(i,0,vec[idx].size())
        {
            if(vec[idx][i].second!=p1 && vec[idx][i].second!=p2){

            if(cnt==0)
                ans = max(ans,vec[idx][i].first+solve(vec,idx+1,cnt+1,vec[idx][i].second,p2));
            else if(cnt==1)
            {
                ans = max(ans,vec[idx][i].first+solve(vec,idx+1,cnt+1,p1,vec[idx][i].second));
            }
            else{
                ans = max(ans,vec[idx][i].first+solve(vec,idx+1,cnt+1,vec[idx][i].second,p2));
            }

            }
        }
        ans = max(ans,solve(vec,idx+1,cnt,p1,p2));
        // cout<<idx<<" ";cout<<p1<<" ";cout<<p2<<" ";cout<<ans<<endl;;
        return t=ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vpi> vec;
        memset(dp,-1,sizeof(dp));
        int n = board.size(),m=board[0].size();
        fori(j,0,m)
        {
            vpi temp;
            fori(i,0,n)
            {
                temp.pb({board[i][j],i});
            }
            dsc(temp);
            while(temp.size()>3) temp.pp();
            vec.pb(temp);
        }
        // for(auto v:vec)
        // {
        //     for(auto it:v)
        //     {
        //         cout<<it.first<<" "<<it.second<<endl;
        //     }
        //     cout<<endl;
        // }
        return solve(vec,0,0,-1,-1);

    }
};
