long long dp[101][101][101][4];

long long fun(int i, int j, int k, int t,vector<vector<int>>& g){
    int n=g.size();
    int m=g[0].size();
    if(t<=0) return 0;
    if(i>=n ) return -5000000000;
    if(dp[i][j][k][t]!=-1e17) return dp[i][j][k][t];

    long long ans=fun(i+1,j,k,t,g);

    for(int l=0;l<m;l++){
        if(l!=j && l!=k){
           if(t==3)  ans=max(ans,g[i][l]+fun(i+1,l,k,2,g));
           else if(t==2)  ans=max(ans,g[i][l]+fun(i+1,j,l,1,g));
            else ans=max(ans,g[i][l]+0LL);
        }
    }

    return dp[i][j][k][t]=ans;
}

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& g) {
        int n=g.size(),m=g[0].size();

        for(int i=0;i<n;i++){
            for(int j=0;j<=m;j++){
                for(int k=0;k<=m;k++){
                    for(int t=0;t<4;t++) dp[i][j][k][t]=-1e17;
                }
            }
        }

        long long ans=fun(0,m,m,3,g);

        return ans;
    }
};
