class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();

        long long temp[3][n], left[3][n], right[3][n];

        long long ans = LONG_LONG_MIN;
        for (int i = 0; i < m; i++) {
            for (int p1 = 0; p1 < n; p1++) {
                temp[0][p1] = board[i][p1];
            }
            for (int j = i + 1; j < m; j++) {
                for (int p2 = 0; p2 < n; p2++) {
                    temp[1][p2] = board[j][p2];
                }
                for (int k = j + 1; k < m; k++) {
                    for (int p3 = 0; p3 < n; p3++) {
                        temp[2][p3] = board[k][p3];
                    }

                    for (int x = 0; x <= 2; x++) {
                        for (int y = 0; y < n; y++) {
                            left[x][y] = y == 0 ? temp[x][0] : max(left[x][y - 1], temp[x][y]);
                        }
                        for (int y = n - 1; y >= 0; y--) {
                            right[x][y] = y == n - 1 ? temp[x][n - 1] : max(right[x][y + 1], temp[x][y]);
                        }
                    }

                    for (int x = 1; x < n - 1; x++) {
                        long long y1 = temp[0][x] + max(left[1][x - 1] + right[2][x + 1], right[1][x + 1] + left[2][x - 1]);
                        long long y2 = temp[1][x] + max(left[0][x - 1] + right[2][x + 1], right[0][x + 1] + left[2][x - 1]);
                        long long y3 = temp[2][x] + max(left[0][x - 1] + right[1][x + 1], right[0][x + 1] + left[1][x - 1]);

                        ans = max({ans, y1, y2, y3});
                    }

                }
            }
        }

        return ans;
    }
};
