class Solution {
    #define ll long long
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();
        vector<vector<pair<ll,ll>>>value(n);
        for(int i = 0;i<a.size();i++){
            vector<pair<ll,ll>>v(m);
            for(int j = 0;j<m;j++){
                v[j] = {a[i][j],j};
            }
            sort(v.begin(),v.end(),greater<pair<ll,ll>>());
            value[i].push_back(v[0]);
            value[i].push_back(v[1]);
            value[i].push_back(v[2]);
        }

        ll ans = -1e17;

        for(int row1 = 0;row1<n;row1++){
            for(int col1 = 0;col1<3;col1++){
                for(int row2 = row1+1;row2<n;row2++){
                    for(int col2 = 0;col2<3;col2++){
                        if(value[row2][col2].second == value[row1][col1].second)continue;
                        for(int row3 = row2+1;row3<n;row3++){
                            for(int col3 = 0;col3<3;col3++){
                                if(value[row2][col2].second == value[row3][col3].second || value[row3][col3].second == value[row1][col1].second)continue;
                                ans = max(ans,value[row1][col1].first+value[row2][col2].first+value[row3][col3].first);
                            }
                        }
                    }
                }
            }
        }

        return ans;

    }
};
