class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<vector<pair<long long,int>>> v(m);

        for (int i = 0; i < m; i++) {
            long long maxi = LLONG_MIN;
            int i1 = -1, i2 = -1, i3 = -1;
            long long second_max = LLONG_MIN;
            long long third_max = LLONG_MIN;

            for (int j = 0; j < n; j++) {
                if (board[i][j] > maxi) {
                    third_max = second_max;
                    i3 = i2;
                    second_max = maxi;
                    i2 = i1;
                    maxi = board[i][j];
                    i1 = j;
                } else if (board[i][j] > second_max) {
                    third_max = second_max;
                    i3 = i2;
                    second_max = board[i][j];
                    i2 = j;
                } else if (board[i][j] > third_max) {
                    third_max = board[i][j];
                    i3 = j;
                }
            }

            v[i].push_back({maxi, i1});
            v[i].push_back({second_max, i2});
            v[i].push_back({third_max, i3});
        }

        long long ans = LLONG_MIN;
        for (int i = 0; i < m - 2; i++) {
            for (int j = i + 1; j < m - 1; j++) {
                for (int k = j + 1; k < m; k++) {
                    for (int l = 0; l < 3; l++) {
                        long long ele1 = v[i][l].first;
                        int index1 = v[i][l].second;
                        for (int p = 0; p < 3; p++) {
                            long long ele2 = v[j][p].first;
                            int index2 = v[j][p].second;
                            for (int q = 0; q < 3; q++) {
                                long long ele3 = v[k][q].first;
                                int index3 = v[k][q].second;
                                if (index1 != index2 && index2 != index3 && index3 != index1) {
                                    ans = max(ans, ele1 + ele2 + ele3);
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
