class Solution {
    int last=0;
    public long maximumValueSum(int[][] board) {
        int m=board.length; int n=board[0].length; 
        last=Math.min(n,6);
        int grid[][]=new int[m][last];
        for(int i=0;i<m;i++){
            PriorityQueue<Pair>pq=new PriorityQueue<>((a,b)->Integer.compare(b.num,a.num));
            for(int j=0;j<n;j++){
                pq.add(new Pair(board[i][j],j));
            }
            int check=0;
            while(check<last){
                grid[i][check++]=pq.peek().idx;
                //System.out.println(board[i][check-1]+" ");
                pq.poll();
            }
        }
        long dp[][][]=new long[m+1][n+1][n+1];
            for(int i=0;i<=m;i++)
                for(int j=0;j<=n;j++)Arrays.fill(dp[i][j],-1l);
        return solve(0,board,grid,-1,-1,0,dp);
    }
    public long solve(int currRow,int board[][],int grid[][],int col1,int col2,int go,long dp[][][]){
        if(go>=3)return 0;
        if(currRow>=board.length)return -1L*10_000_000_000L;
        if(dp[currRow][col1+1][col2+1]!=-1)return dp[currRow][col1+1][col2+1];
        long max=solve(currRow+1,board,grid,col1,col2,go,dp);
        for(int i=0;i<grid[currRow].length;i++){
            int idx=grid[currRow][i];
            //System.out.println(currRow+" "+idx+" "+i);
            int curr=board[currRow][idx];
            if((idx==col1)||(idx==col2))continue;
            if(col1==-1){
                long ans=solve(currRow+1,board,grid,idx,col2,go+1,dp);
                if(ans!=-1L*10_000_000_000L)
                max=Math.max(max,curr+ans);
            }
            else{
                long ans=solve(currRow+1,board,grid,col1,idx,go+1,dp);
                if(ans!=-1L*10_000_000_000L)
                max=Math.max(max,curr+ans);
            }
        }
        return dp[currRow][col1+1][col2+1]=max;
    }
}
class Pair{
    int num;
    int idx;
    Pair(int num,int idx){
        this.num=num;
        this.idx=idx;
    }
}
