class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=-1e17;
        int n=board.size();
        int m=board[0].size();

        vector<vector<int>>row;
        for(int i=0;i<n;i++)
        {
            vector<pair<int,int>>cur;
            for(int j=0;j<m;j++)
            {
                cur.push_back({board[i][j],j});
            }
            sort(cur.rbegin(),cur.rend());
            row.push_back({cur[0].second,cur[1].second,cur[2].second});
        }
        // for(int i=0;i<n;i++)
        // {
        //     cout<<i<<"-->";
        //     for(auto &j:row[i])
        //     {
        //         cout<<j<<" ";
        //     }
        //     cout<<endl;

        // }

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(i==j)
                {
                    continue;
                }
                for(int k=0;k<n;k++)
                {
                    if(k==i||k==j)
                    {
                        continue;
                    }
                    for(int x=0;x<3;x++)
                    {
                        int firstcol=row[i][x];
                        for(int y=0;y<3;y++)
                        {
                            int secondcol=row[j][y];
                            if(secondcol==firstcol)
                            {
                                continue;
                            }
                            for(int z=0;z<3;z++)
                            {
                                int thirdcol=row[k][z];
                                if(thirdcol==firstcol||thirdcol==secondcol)
                                {
                                    continue;
                                }
                                long long cur=board[i][firstcol];
                                cur+=board[j][secondcol];
                                cur+=board[k][thirdcol];
                                ans=max(ans,cur);
                            }
                        }
                    }
                }
            }

        }
        return ans;
    }
};
