#include <vector>
#include <climits>
using namespace std;

#define pb push_back

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<int>> maxi(n, vector<int>(6, INT_MIN));
        for (int i = 0; i < n; i++) {
            int maxin = INT_MIN, secondmaxi = INT_MIN, thirdmaxi = INT_MIN;
            int maxiInd = -1, secondMaxiInd = -1, thirdMaxiInd = -1;
            for (int j = 0; j < m; j++) {
                if (board[i][j] > maxin) {
                    thirdmaxi = secondmaxi;
                    thirdMaxiInd = secondMaxiInd;

                    secondmaxi = maxin;
                    secondMaxiInd = maxiInd;

                    maxin = board[i][j];
                    maxiInd = j;
                } else if (board[i][j] > secondmaxi) {
                    thirdmaxi = secondmaxi;
                    thirdMaxiInd = secondMaxiInd;

                    secondmaxi = board[i][j];
                    secondMaxiInd = j;
                } else if (board[i][j] > thirdmaxi) {
                    thirdmaxi = board[i][j];
                    thirdMaxiInd = j;
                }
            }
            maxi[i] = {maxin, maxiInd, secondmaxi, secondMaxiInd, thirdmaxi, thirdMaxiInd};
        }
        long long ans = LLONG_MIN;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                for (int k = j + 1; k < n; k++) {
                    for (int l = 1; l < 6; l += 2) {
                        for (int m = 1; m < 6; m += 2) {
                            for (int n = 1; n < 6; n += 2) {
                                if (maxi[i][l] == maxi[j][m] || maxi[i][l] == maxi[k][n] || maxi[k][n] == maxi[j][m]) continue;
                                ans = max(ans, 1LL * maxi[i][l-1] + maxi[j][m-1] + maxi[k][n-1]);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
