class Solution:
    def getMax(self, b1, b2, b3):
        s = -math.inf
        for i in b1[:3]:
            for j in b2[:3]:
                for k in b3[:3]:
                    if i[1] != j[1] and j[1] != k[1] and i[1] != k[1]:
                        # print(i, j,k)
                        s = max(s, i[0] + j[0] + k[0])
        return s
    def maximumValueSum(self, board: List[List[int]]) -> int:
        for i in range(len(board)):
            board[i] = [[n, j] for j, n in enumerate(board[i])]
            board[i].sort(reverse=True)

        s = -math.inf
        for i in range(len(board)):
            for j in range(i+1,len(board)):
                for k in range(j+1, len(board)):
                    s = max(s, self.getMax(board[i], board[j], board[k]))
        return s
