type Node struct {
    val int
    col int
}

func getTop3(row []int) []*Node {
    var res = make([]*Node, 0)

    var fiIndex, fiVal = -1, -1000000001
    for i, rowVal := range row {
        if fiVal <= rowVal {
            fiVal = rowVal
            fiIndex = i
        }
    }
    res = append(res, &Node{fiVal, fiIndex})

    var seIndex, seVal = -1, -1000000001
    for i, rowVal := range row {
        if seVal <= rowVal && i != fiIndex {
            seVal = rowVal
            seIndex = i
        }
    }
    res = append(res, &Node{seVal, seIndex})

    var thIndex, thVal = -1, -1000000001
    for i, rowVal := range row {
        if thVal <= rowVal && i != fiIndex && i != seIndex {
            thVal = rowVal
            thIndex = i
        }
    }
    res = append(res, &Node{thVal, thIndex})

    return res
}

func maximumValueSum(board [][]int) int64 {
    var rows = make([][]*Node, len(board))

    for i := range board {
        rows[i] = getTop3(board[i])
    }

    var max int64 = -5000000005

    for i := 0; i < len(rows); i++ {
        for j := 0; j < len(rows); j++ {
            for k := 0; k < len(rows); k++ {
                if i == j || j == k || i == k {
                    continue
                }

                for ii := 0; ii < 3; ii++ {
                    for jj := 0; jj < 3; jj++ {
                        for kk := 0; kk < 3; kk++ {
                            if rows[i][ii].col == rows[j][jj].col || rows[i][ii].col == rows[k][kk].col || rows[j][jj].col == rows[k][kk].col {
                                continue
                            }

                            var currentSum = int64(rows[i][ii].val) + int64(rows[j][jj].val) + int64(rows[k][kk].val)
                            if currentSum > max {
                                max = currentSum
                            }
                        }
                    }
                }
            }
        }
    }

    return max
}
