class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:

        m, n = len(board), len(board[0])

        bestrows = []

        for i in range(m):
            bestrows.append(sorted([(v, j) for j, v in enumerate(board[i])], reverse = True)[:3])


        res = -math.inf

        for a in range(m):

            for i in range(a + 1, m):
                    if i == a:
                        continue

                    for i2 in range(i + 1, m):
                        if i2 == a or i2 == i:
                            continue

                        for (poss0, b) in bestrows[a]:

                            for (poss1, col1) in bestrows[i]:

                                for (poss2, col2) in bestrows[i2]:

                                    if len({b, col1, col2}) == 3:
                                        res = max(res, poss0 + poss1 + poss2)

        return res
