class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])
        if m > n:
            # transpose
            board = [[board[j][i] for j in range(m)] for i in range(n)]
            m, n = n, m

        ans = float('-inf')

        candidates = [None for _ in range(m)]
        for i in range(m):
            candidates[i] = sorted(zip(board[i], list(range(n))), reverse=True)[:3]

        # the first rock
        for i1 in range(m-2):
            for v1, c1 in candidates[i1]:
                for i2 in range(i1+1, m-1):
                    for v2, c2 in candidates[i2]:
                        if c2 == c1:
                            continue                    
                        for i3 in range(i2+1, m):
                            for v3, c3 in candidates[i3]:
                                if c3 == c2 or c3 == c1:
                                    continue

                                ans = max(v1+v2+v3, ans)

        return ans
