class Solution {
public:
    long long f(vector<pair<int,int>>&a,vector<pair<int,int>>& b,vector<pair<int,int>>& c){
        long long sum = LLONG_MIN;
        //cout<<a.size()<<endl;
        for(int i=0;i<3;i++){
            for(int j=0;j<3;j++){
                for(int k=0;k<3;k++){
                    if(a[i].second != b[j].second && b[j].second != c[k].second && c[k].second != a[i].second){
                        sum = max(sum,(long long) a[i].first + b[j].first + c[k].first);
                    }
                }
            }
        }
        return sum;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size() , n = board[0].size();
        vector<vector<pair<int,int>>> v(m,vector<pair<int,int>>(n));
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                v[i][j] = {board[i][j],j};
            }
        }
        for(auto &i:v){
            sort(i.begin(),i.end(),greater<pair<int,int>>());
        }
        long long sum = LLONG_MIN;
        for(int i=0;i<m;i++){
            for(int j=0;j<m;j++){
                for(int k=0;k<m;k++){
                    if(i !=j && j!=k && k!=i){
                        sum = max(sum,f(v[i],v[j],v[k]));
                    }
                }
            }
        }
        return sum;
    }
};
