class Solution {
public:

    long long solve(int r,int n,int m,vector<vector<int>>& board,int blocked1,int blocked2,int k,vector<vector<vector<vector<long long>>>>&dp,vector<vector<int>>&v){
        if(k==0){
            return 0;
        }
        if(r==n){
            return LLONG_MIN/2;
        }
        if(dp[r][blocked1+1][blocked2+1][k]!=-1){
            return dp[r][blocked1+1][blocked2+1][k];
        }
        long long ans=solve(r+1,n,m,board,blocked1,blocked2,k,dp,v);
        for(int i=0;i<3;i++){
            int c=v[r][i];
            if(blocked1==-1){
                ans=max(ans,(long long)board[r][c]+solve(r+1,n,m,board,c,blocked2,k-1,dp,v));
            }
            else if(blocked2==-1){
                if(c==blocked1){
                    continue;
                }
                ans=max(ans,(long long)board[r][c]+solve(r+1,n,m,board,blocked1,c,k-1,dp,v));
            }
            else{
                if(c==blocked1 || c==blocked2){
                    continue;
                }
                ans=max(ans,(long long)board[r][c]+solve(r+1,n,m,board,blocked1,blocked2,k-1,dp,v));
            }
        }
        return dp[r][blocked1+1][blocked2+1][k]=ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
        cout.tie(NULL);
        int n=board.size(),m=board[0].size();

        vector<vector<int>>v(n);
        for(int i=0;i<n;i++){
            priority_queue<pair<int,int>>q;
            for(int j=0;j<m;j++){
                q.push({board[i][j],j});
            }
            for(int j=0;j<3;j++){
                v[i].push_back(q.top().second);
                q.pop();
                // cout<<v[i].back()<<" ";
            }
            // cout<<endl;
        }
        // cout<<endl;

        vector<vector<vector<vector<long long>>>>dp(n,vector<vector<vector<long long>>>(m+1,vector<vector<long long>>(m+1,vector<long long>(4,-1))));
        return solve(0,n,m,board,-1,-1,3,dp,v);
    }
};
