class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length;
        int n = board[0].length;
        long maxSum = Long.MIN_VALUE;

        int[][][] maxValues = new int[m][3][2];

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < 3; j++) {
                maxValues[i][j][0] = Integer.MIN_VALUE;
            }
            for (int j = 0; j < n; j++) {
                if (board[i][j] > maxValues[i][0][0]) {
                    maxValues[i][2][0] = maxValues[i][1][0];
                    maxValues[i][2][1] = maxValues[i][1][1];
                    maxValues[i][1][0] = maxValues[i][0][0];
                    maxValues[i][1][1] = maxValues[i][0][1];
                    maxValues[i][0][0] = board[i][j];
                    maxValues[i][0][1] = j;
                } else if (board[i][j] > maxValues[i][1][0]) {
                    maxValues[i][2][0] = maxValues[i][1][0];
                    maxValues[i][2][1] = maxValues[i][1][1];
                    maxValues[i][1][0] = board[i][j];
                    maxValues[i][1][1] = j;
                } else if (board[i][j] > maxValues[i][2][0]) {
                    maxValues[i][2][0] = board[i][j];
                    maxValues[i][2][1] = j;
                }
            }
        }

        // 枚举所有可能的三行组合
        for (int i1 = 0; i1 < m; i1++) {
            for (int i2 = i1 + 1; i2 < m; i2++) {
                for (int i3 = i2 + 1; i3 < m; i3++) {
                    for (int x = 0; x < 3; x++) {
                        for (int y = 0; y < 3; y++) {
                            for (int z = 0; z < 3; z++) {
                                if (maxValues[i1][x][1] != maxValues[i2][y][1] &&
                                    maxValues[i1][x][1] != maxValues[i3][z][1] &&
                                    maxValues[i2][y][1] != maxValues[i3][z][1]) {
                                    maxSum = Math.max(maxSum, (long) maxValues[i1][x][0] + maxValues[i2][y][0] + maxValues[i3][z][0]);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;

    }
}
