class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n=a.size();
        int m=a[0].size();
        vector<vector<pair<int,int>>>v(n,vector<pair<int,int>>(3));
        for(int i=0;i<n;i++)
        {
            multiset<pair<int,int>>s;
            for(int j=0;j<m;j++)
            {
                s.insert({a[i][j],j});
                if(s.size()>3)
                s.erase(s.begin());
            }
            int sz=2;
            for(auto j:s)
            {
                v[i][sz]={j.first,j.second};
                sz--;
            }
        }
         long long int ans=-1e15;
        for(int i=0;i<n;i++)
        {
            for(int j=i+1;j<n;j++)
            {
                for(int k=j+1;k<n;k++)
                {

                    for(int x=0;x<3;x++)
                    {
                        for(int y=0;y<3;y++)
                        {
                            for(int z=0;z<3;z++)
                            {
                                if(v[i][x].second!=v[j][y].second && v[i][x].second!=v[k][z].second && v[j][y].second!=v[k][z].second)
                                {
                                    long long int temp=(long long int)v[i][x].first;
                                    temp+=(long long int)v[j][y].first;
                                    temp+=(long long int)v[k][z].first;
                                    ans=max(ans,temp);
                                }
                            }
                        }
                    }

                }
            }
        }
        return ans;
    }
};
