var maximumValueSum = function(board) {

    let n = board.length;
    let m = board[0].length;
    let tops = board.map((row,i) => top3(row, i, m));

    let score = -Infinity;
    for (let a = 0; a < n; ++a) {
      for (let b = a+1; b < n; ++b) {
        for (let c = b+1; c < n; ++c) {
        for (let aa = 0; aa < 3; ++aa) {
        for (let bb = 0; bb < 3; ++bb) {
        for (let cc = 0; cc < 3; ++cc) {
            if (valid(tops[a][aa], tops[b][bb], tops[c][cc])) {
                score = Math.max(score, tops[a][aa][0] + tops[b][bb][0] + tops[c][cc][0]);
            }

    }
    }
    }
    }
    }  
    }

    return score;
};


function valid(a,b,c) {
    return a[1] != b[1] && b[1] != c[1] && c[1] != a[1] && a[2] != b[2] && b[2] != c[2] && c[2] != a[2]; 
}



function top3(arr, r, n) {
    let ans = [[-Infinity, r, -1], [-Infinity, r, -1], [-Infinity,r,-1]];

    for (let i = 0; i < n; ++i) {
     update(ans, arr[i],i, r);
    }

    return ans;
}

function update(ans, val,i, r) {

    if (val > ans[2][0]) {
        ans[0] = ans[1];
        ans[1] = ans[2];
        ans[2] = [val, r, i];
    } else if (val > ans[1][0]) {
        ans[0] = ans[1];
        ans[1] = [val, r, i];
    } else if (val > ans[0][0]) {
        ans[0] = [val, r, i];
    }

    return ans;


}
