class Solution {
public:
    struct ran{
        int x, y, z;
        bool operator < (const ran &a)const{
            return z > a.z;
        }
    };
    long long maximumValueSum(vector<vector<int>>& tr) {
        int n = tr.size(), m = tr[0].size();
        vector<ran>v;
        for(int i = 0; i < n; ++i){
            for(int j = 0; j < m; ++j){
                v.push_back({i, j, tr[i][j]});
            }
        }
        sort(v.begin(), v.end());
        int len = min(305, (int)v.size());
        long long ans = -3e9;
        for(int i = 0; i < len; ++i){
            for(int j = i + 1; j < len; ++j){
                for(int k = j + 1; k < len; ++k){
                    if(v[i].x != v[j].x && v[i].x != v[k].x && v[j].x != v[k].x
                    &&v[i].y != v[j].y && v[i].y != v[k].y && v[j].y != v[k].y){
                        long long sum = v[i].z;
                        sum += v[j].z + v[k].z;
                        ans = max(ans, sum);
                        // cout << v[i].z << " " << v[j].z << " " << v[k].z << endl;
                    }
                }
            }
        }
        return ans;
    }
};
