class Solution {
public:
    typedef long long LL;
    const LL inf=0x3f3f3f3f3f3f3f3f;
    const static int N=500+10;
    vector<vector<LL>> row_mx[N];
    long long maximumValueSum(vector<vector<int>>& board)
    {
        int n=board.size();
        int m=board[0].size();
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                row_mx[i].push_back({board[i][j],j});
            }
            sort(row_mx[i].begin(),row_mx[i].end());
            reverse(row_mx[i].begin(),row_mx[i].end());
        }
        LL ans=-inf;
        for(int i=0;i<n;i++)
        {
            for(int j=i+1;j<n;j++)
            {
                for(int k=j+1;k<n;k++)
                {
                    for(int a=0;a<3;a++)
                    {
                        for(int b=0;b<3;b++)
                        {
                            for(int c=0;c<3;c++)
                            {
                                if(row_mx[i][a][1]!=row_mx[j][b][1] && row_mx[j][b][1]!=row_mx[k][c][1] && row_mx[i][a][1]!=row_mx[k][c][1])
                                {
                                    LL res=row_mx[i][a][0]+row_mx[j][b][0]+row_mx[k][c][0];
                                    ans=max(ans,res);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
