#include <bits/stdc++.h>
using namespace std;


class Solution {
    class Cell {
    public:
        int val, x, y;
        Cell(int val, int x, int y) : val(val), x(x), y(y) {}
    };

public:
    long long maximumValueSum(std::vector<std::vector<int>>& board) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);   

        int m = board.size(), n = board[0].size();
        std::vector<Cell> cells;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                cells.emplace_back(board[i][j], i, j);
            }
        }

        std::sort(cells.begin(), cells.end(), [](const Cell& c1, const Cell& c2) {
            return c2.val < c1.val;
        });

        long long ans = std::numeric_limits<long long>::min();
        long long val1 = std::numeric_limits<long long>::min(), val2 = std::numeric_limits<long long>::min(), val3 = std::numeric_limits<long long>::min();
        int s = cells.size();
        for (int i = 0; i < s; i++) {
            int r1 = cells[i].x, c1 = cells[i].y;
            val1 = cells[i].val;
            for (int j = i + 1; j < s; j++) {
                int r2 = cells[j].x, c2 = cells[j].y;
                if (!(r2 == r1 || c2 == c1)) {
                    val2 = cells[j].val;
                    for (int k = j + 1; k < s; k++) {
                        int r3 = cells[k].x, c3 = cells[k].y;
                        if (!(r3 == r1 || c3 == c1 || r3 == r2 || c3 == c2)) {
                            val3 = cells[k].val;
                            ans = std::max(ans, val1 + val2 + val3);
                            break;
                        }
                    }
                }
            }
        }
        return ans;
    }
};
