class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m1 = board.size();
        int n1 = board[0].size();
        long long maxSum = LLONG_MIN;
        vector<vector<long long>> bestValues(m1, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> bestColumn(m1, vector<int>(3, -1));

        for (int i1 = 0; i1 < m1; ++i1) {
            vector<pair<long long, int>> colValues;
            for (int j1 = 0; j1 < n1; ++j1) {
                colValues.emplace_back(board[i1][j1], j1);
            }
            sort(colValues.rbegin(), colValues.rend()); 

            for (int k1 = 0; k1 < 3 && k1 < n1; ++k1) {
                bestValues[i1][k1] = colValues[k1].first;
                bestColumn[i1][k1] = colValues[k1].second;
            }
        }

        // Try all combinations of all  3 different rows.
        for (int r1 = 0; r1 < m1; ++r1) {
            for (int r2 = r1 + 1; r2 < m1; ++r2) {
                for (int r3 = r2 + 1; r3 < m1; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (bestColumn[r2][j] == bestColumn[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (bestColumn[r3][k] == bestColumn[r1][i] || bestColumn[r3][k] == bestColumn[r2][j]) continue;

                                long long currentSum = bestValues[r1][i] + bestValues[r2][j] + bestValues[r3][k];
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
