from itertools import combinations

class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])

        rmv = []
        for row in range(m):
            row_values = sorted([(board[row][col], col) for col in range(n)], reverse=True)
            rmv.append(row_values[:3])

        max_value = float('-inf')
        for r1 in range(m):
            for r2 in range(r1 + 1, m):
                for r3 in range(r2 + 1, m):
                    for (v1, c1) in rmv[r1]:
                        for (v2, c2) in rmv[r2]:
                            if c1 != c2:
                                for (v3, c3) in rmv[r3]:
                                    if c3 != c1 and c3 != c2:
                                        max_value = max(max_value, v1 + v2 + v3)

        return max_value
