class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>> pq(n);
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                pq[i].push({board[i][j],j});
                if(pq[i].size() > 3){
                    pq[i].pop();
                }
            }
        }
        long long ans = LONG_LONG_MIN;
        vector<vector<int>> st(n,vector<int>(3));
        for(int i = 0; i < n; i++){
            st[i][0] = pq[i].top().second;
            pq[i].pop();
            st[i][1] = pq[i].top().second;
            pq[i].pop();
            st[i][2] = pq[i].top().second;
            pq[i].pop();
        }

        for(int i = 0; i < n-2; i++){
            for(int j = i+1; j < n-1; j++){
                for(int k = j +1; k < n; k++ ){
                    for(int in = 0; in < 3; in++){
                        for(int jn = 0; jn < 3; jn++){
                            for(int kn = 0; kn < 3; kn++){
                                if(st[i][in] != st[j][jn] && st[i][in] != st[k][kn] && st[k][kn] != st[j][jn]){
                                    ans = max(ans,1LL*board[i][st[i][in]]+1LL*board[j][st[j][jn]]+1LL*board[k][st[k][kn]]);
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
