class Solution {
public:

    vector<vector<vector<long long>>> dp;

    long long solve(vector<vector<int>>& v, int r, int p1, int p2, int p3){
        if(r == v.size()) return -1e18;

        if(dp[r][p1 + 1][p2 + 1] != -1) return dp[r][p1 + 1][p2 + 1];

        int m = v[0].size();

        long long s = -1e18;

        if(p1 == -1){
            s = max(s, solve(v,r + 1,p1,p2,p3));
            for(int i = 0; i < m; i++){
                s = max(s, v[r][i] + solve(v,r + 1,i,p2,p3));
            }
        }
        else if(p2 == -1){
            s = max(s, solve(v,r + 1,p1,p2,p3));
            for(int i = 0; i < m; i++){
                if(i == p1) continue;
                s = max(s, v[r][i] + solve(v,r + 1,p1,i,p3));
            }
        }
        else if(p3 == -1){
            s = max(s, solve(v,r + 1,p1,p2,p3));
            long long a = -1e18;
            for(int i = 0; i < m; i++){
                if(i == p1 || i == p2) continue;
                long long t = v[r][i];
                a = max(a, t);
            }
            s = max(s, a);
        }

        return dp[r][p1 + 1][p2 + 1] = s;
    }

    long long maximumValueSum(vector<vector<int>>& v) {
        int n = v.size(), m = v[0].size();
        dp.resize(n, vector<vector<long long>> (m + 1, vector<long long> (m + 1, -1)));
        long long ans = solve(v,0,-1,-1,-1);
        return ans;
    }
};
