class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        max_value = float('-inf')

        # 预处理每行的前三大值
        row_max_values = []
        for r in range(m):
            values = sorted([(board[r][c], c) for c in range(n)], reverse=True)[:3]
            row_max_values.append(values)

        # 枚举第一行
        for r1 in range(m):
            v1, c1 = row_max_values[r1][0]
            v1_alt1, c1_alt1 = row_max_values[r1][1]
            v1_alt2, c1_alt2 = row_max_values[r1][2]

            # 枚举第二行
            for r2 in range(r1 + 1, m):
                v2, c2 = row_max_values[r2][0]
                v2_alt1, c2_alt1 = row_max_values[r2][1]
                v2_alt2, c2_alt2 = row_max_values[r2][2]
                # 枚举第三行
                for r3 in range(r2 + 1, m):
                    v3, c3 = row_max_values[r3][0]
                    v3_alt1, c3_alt1 = row_max_values[r3][1]
                    v3_alt2, c3_alt2 = row_max_values[r3][2]

                    # 检查列冲突情况，并计算最大可能的和
                    if c1 != c2 and c1 != c3 and c2 != c3:
                        max_value = max(max_value, v1 + v2 + v3)
                        continue
                    elif c1 == c2 and c1 != c3:
                        ## 换c1 (c2, c3)
                        if c1_alt1 == c2 or c1_alt1 == c3:
                            max_value = max(max_value, v1_alt2 + v2 + v3)
                        else:
                            max_value = max(max_value, v1_alt1 + v2 + v3)
                        ## 换c2
                        if c2_alt1 == c1 or c2_alt1 == c3:
                            max_value = max(max_value, v2_alt2 + v1 + v3)
                        else:
                            max_value = max(max_value, v2_alt1 + v1 + v3)
                    elif c1 == c3 and c1 != c2:
                        # 换 c1
                        if c1_alt1 == c2 or c1_alt1 == c3:
                            max_value = max(max_value, v1_alt2 + v2 + v3)
                        else:
                            max_value = max(max_value, v1_alt1 + v2 + v3)
                        ## 换c3
                        if c3_alt1 == c1 or c3_alt1 == c2:
                            max_value = max(max_value, v3_alt2 + v1 + v2)
                        else:
                            max_value = max(max_value, v3_alt1 + v1 + v2)
                    elif c2 == c3 and c2 != c1:
                        # 换c2
                        if c2_alt1 == c1 or c2_alt1 == c2:
                            max_value = max(max_value, v2_alt2 + v3 + v1)
                        else:
                            max_value = max(max_value, v2_alt1 + v3 + v1)
                        ## 换c3
                        if c3_alt1 == c1 or c3_alt1 == c2:
                            max_value = max(max_value, v3_alt2 + v1 + v2)
                        else:
                            max_value = max(max_value, v3_alt1 + v1 + v2)
                    # else: ## c2 == c3 and c1 == c3
                    if c1 != c2_alt1 and c1 != c3_alt1 and c2_alt1 != c3_alt1:
                        max_value = max(max_value , v1 + v2_alt1 + v3_alt1)
                    if c1 != c2_alt1 and c1 != c3_alt2 and c2_alt1 != c3_alt2:
                        max_value = max(max_value , v1 + v2_alt1 + v3_alt2)
                    if c1 != c2_alt2 and c1 != c3_alt1 and c2_alt2 != c3_alt1:
                        max_value = max(max_value , v1 + v2_alt2 + v3_alt1)
                    if c1 != c2_alt2 and c1 != c3_alt2 and c2_alt2 != c3_alt2:
                        max_value = max(max_value , v1 + v2_alt2 + v3_alt2)
                    if c2 != c1_alt1 and c2 != c3_alt1 and c1_alt1 != c3_alt1:
                        max_value = max(max_value , v2 + v1_alt1 + v3_alt1)
                    if c2 != c1_alt1 and c2 != c3_alt2 and c1_alt1 != c3_alt2:
                        max_value = max(max_value , v2 + v1_alt1 + v3_alt2)
                    if c2 != c1_alt2 and c2 != c3_alt1 and c1_alt2 != c3_alt1:
                        max_value = max(max_value , v2 + v1_alt2 + v3_alt1)
                    if c2 != c1_alt2 and c2 != c3_alt2 and c1_alt2 != c3_alt2:
                        max_value = max(max_value , v2 + v1_alt2 + v3_alt2)
                    if c3 != c2_alt1 and c3 != c1_alt1 and c2_alt1 != c1_alt1:
                        max_value = max(max_value , v3 + v2_alt1 + v1_alt1)
                    if c3 != c2_alt1 and c3 != c1_alt2 and c2_alt1 != c1_alt2:
                        max_value = max(max_value , v3 + v2_alt1 + v1_alt2)
                    if c3 != c2_alt2 and c3 != c1_alt1 and c3_alt2 != c1_alt1:
                        max_value = max(max_value , v3 + v2_alt2 + v1_alt1)
                    if c3 != c2_alt2 and c3 != c1_alt2 and c2_alt2 != c1_alt2:
                        max_value = max(max_value , v3 + v2_alt2 + v1_alt2)

        return max_value
