typedef long long ll;

const int N = 123;


class Solution {
    vector<vector<int>> mx;
public:
    long long maximumValueSum(const vector<vector<int>>& g) {
        mx = g;
        int m = g.begin()->size();
        int n = g.size();
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                mx[i][j] = j;
            }
            sort(mx[i].begin(), mx[i].end(), [&](const int &a, const int &b) -> bool {
               return g[i][a] > g[i][b];
            });
        }
        vector<int> lines(3);
        set<int> hack;
        long long ans = -(1ll << 60);
        for (int i = 0; i < g.size(); i++) {
            lines[0] = i;
            for (int j = i + 1; j < g.size(); j++) {
                lines[1] = j;
                for (int k = j + 1; k < g.size(); k++) {
                    lines[2] = k;
                    auto l0mx = mx[lines[0]][0], l1mx = mx[lines[1]][0], l2mx = mx[lines[2]][0];
                    if (l0mx != l1mx && l0mx != l2mx && l1mx != l2mx) {
                        ans = max(ans, 1ll * g[lines[0]][l0mx] + g[lines[1]][l1mx] + g[lines[2]][l2mx]);
                        continue;
                    }
                    vector<int> order{0, 1, 2};
                    do {
                        ll sum = 0;
                        hack.clear();
                        for (int x = 0; x < 3; x++) {
                            const auto &mlox = mx[lines[order[x]]];
                            for (int y = 0; y < m; y++) {
                                if (hack.contains(mlox[y])) {
                                    continue;
                                }
                                hack.insert(mlox[y]);
                                auto ttt = g[lines[order[x]]][mlox[y]];
                                sum += ttt;
                                break;
                            }
                        }
                        ans = max(sum, ans);
                    } while (next_permutation(order.begin(), order.end()));
                }
            }
        }
        return ans;
    }
};
