func maximumValueSum(board [][]int) int64 {
	type Pair struct {
		x, y, v int
	}
	arrs := [][]Pair{}
	for x, arr := range board {
		tmp := []Pair{}
		for y, v := range arr {
			tmp = append(tmp, Pair{x, y, v})
		}
		sort.Slice(tmp, func(i, j int) bool { return tmp[i].v < tmp[j].v })
		for i, j := 0, len(tmp)-1; i < j; i, j = i+1, j-1 {
			tmp[i], tmp[j] = tmp[j], tmp[i]
		}

		arrs = append(arrs, tmp)
	}
	theRes := math.MinInt
	for i := 0; i < len(board); i++ {
		for j := i + 1; j < len(board); j++ {
			for k := j + 1; k < len(board); k++ {
				t := 3
				a := arrs[i][:t]
				b := arrs[j][:t]
				c := arrs[k][:t]

				for _, va := range a {
					for _, vb := range b {
						for _, vc := range c {
							c1 := va.x == vb.x || vb.x == vc.x || va.x == vc.x
							c2 := va.y == vb.y || vb.y == vc.y || va.y == vc.y

							//!
							// if va.v+vb.v+vc.v == 229 {
							// 	fmt.Println("<--")
							// 	fmt.Println(va, vb, vc)
							// }

							if c1 || c2 {
								continue
							} else {
								tmpv := va.v + vb.v + vc.v
								theRes = max(theRes, tmpv)
							}
						}
					}
				}
			}
		}
	}
	return int64(theRes)
}

func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}
func abs(a int) int {
	if a > 0 {
		return a
	}
	return -a
}

func min(a, b int) int {
	if a < b {
		return a
	}
	return b
