class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<vector<pair<int,int>>> row(n, vector<pair<int,int>>(m));        
        for(int i = 0;i<n;i++){
            for(int j = 0;j<m;j++){
                row[i][j] = {board[i][j], j};
            }
        }
        for(int i = 0;i<n;i++){
            sort(row[i].begin(), row[i].end(), greater<pair<int,int>>());
        }
        int mx1 = 0;
        int i1 = -1;
        int j1 = -1;
        set<pair<int,int>> s;
        for(int i = 0;i<n;i++){
            for(int j = 0;j<3;j++){
                mx1 = max(mx1, row[i][j].first);
            }
        }
        long long ans = -1e12;
        for(int i3 = 0;i3<n;i3++){
            for(int j3 = 0;j3<3;j3++){
            for(int i = 0;i<n;i++){
                for(int j= 0;j<3;j++){
                    if(i!=i3 && row[i][j].second != row[i3][j3].second){
                        for(int i2 = 0;i2<n;i2++){
                            for(int j2 = 0;j2<3;j2++){
                                if(i2 != i && i2!=i3 && row[i2][j2].second != row[i][j].second && row[i2][j2].second != row[i3][j3].second){
                                    if(ans <= (long long)board[i3][row[i3][j3].second] + board[i][row[i][j].second] + board[i2][row[i2][j2].second]){
