class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n=a.size(); int m=a[0].size();
        vector<pair<long long,pair<int,int>>> v;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v.push_back({a[i][j],{i,j}});
            }
        }
        long long fans=-1e17;
        sort(v.rbegin(),v.rend());
        int t=v.size();
        int g=min(t,3*(m+n));
        for(int i=0;i<g;i++){
            for(int j=i+1;j<g;j++){
                for(int k=j+1;k<g;k++){
                    if(v[i].second.first!=v[j].second.first && v[i].second.first!=v[k].second.first &&
                      v[k].second.first!=v[j].second.first && v[i].second.second!=v[j].second.second && v[i].second.second!=v[k].second.second &&
                      v[k].second.second!=v[j].second.second)
                        fans=max(fans,v[i].first+v[j].first+v[k].first+0ll);
                }
            }
        }
        return fans;
    }
};
