class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = LONG_MIN;
        int r = board.size();
        int c = board[0].size();

        vector<vector<pair<long long, int>>> mxval(
            r, vector<pair<long long, int>>(3));

        for (int i = 0; i < r; i++) {
            vector<pair<int, int>> curr;
            for (int j = 0; j < c; j++) {
                curr.push_back({board[i][j], j});
            }

            sort(curr.begin(), curr.end(),
                 [](const pair<long long, int>& a,
                    const pair<long long, int>& b) {
                     return a.first > b.first;
                 });

            for (int k = 0; k < 3; k++) {
                mxval[i][k].first = curr[k].first;
                mxval[i][k].second = curr[k].second;
            }
        }

        for (int i1 = 0; i1 < r; i1++) {
            for (int i2 = i1 + 1; i2 < r; i2++) {
                for (int i3 = i2 + 1; i3 < r; i3++) {

                    for (int k1 = 0; k1 < 3; k1++) {
                        for (int k2 = 0; k2 < 3; k2++) {
                            for (int k3 = 0; k3 < 3; k3++) {
                                if (mxval[i1][k1].second ==
                                        mxval[i2][k2].second ||
                                    mxval[i1][k1].second ==
                                        mxval[i3][k3].second ||
                                    mxval[i2][k2].second ==
                                        mxval[i3][k3].second) {
                                    continue;
                                }

                                long long currsum = mxval[i1][k1].first +
                                                    mxval[i2][k2].first +
                                                    mxval[i3][k3].first;
                                ans = max(ans, currsum);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
