class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = (int)board.size(), n = (int)board[0].size();
        long long maxi = LLONG_MIN;
        vector<vector<pair<long long, int>>> maxForRow(m);

        for (int i = 0; i < m; i++) {
            pair<long long, int> maxi1 = {LLONG_MIN, -1}, maxi2 = {LLONG_MIN, -1},
                           maxi3 = {LLONG_MIN, -1};
            for (int j = 0; j < n; j++) {
                if (board[i][j] > maxi1.first) {
                    maxi3 = maxi2;
                    maxi2 = maxi1;
                    maxi1 = {board[i][j], j};
                } else if (board[i][j] > maxi2.first) {
                    maxi3 = maxi2;
                    maxi2 = {board[i][j], j};
                } else if (board[i][j] > maxi3.first) {
                    maxi3 = {board[i][j], j};
                }
            }
            maxForRow[i] = {maxi1, maxi2, maxi3};
        }

        // for(int i = 0; i < )

        for(int i = 0; i < m; i++)
        {
            for(int j = i + 1; j < m; j++)
            {
                for(int k = j + 1; k < m; k++)
                {
                    for(int m = 0; m < 3; m++)
                    {
                        for(int n = 0; n < 3; n++)
                        {
                            for(int o = 0; o < 3; o++)
                            {
                                pair<long long, int> v1, v2, v3;
                                v1 = maxForRow[i][m];
                                v2 = maxForRow[j][n];
                                v3 = maxForRow[k][o];

                                if(v1.second != v2.second && v1.second != v3.second && v2.second != v3.second)
                                {
                                    maxi = max(maxi, v1.first + v2.first + v3.first);
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxi;
    }
};
