class Solution {
    public long maximumValueSum(int[][] board) {
       int n = board.length;
        int m = board[0].length;

        List<int[]>[] list = new List[n];
        for (int i = 0; i < n; i++) {

            int[][] curr = new int[m][2];
            for (int j = 0; j < m; j++) {
                curr[j][0] = j;
                curr[j][1] = board[i][j];
            }
            Arrays.sort(curr, (a, b) -> {
                if (a[1] == b[1]) {
                    return a[0] - a[1];
                }
                return b[1] - a[1];
            });
            list[i] = new ArrayList<>();
            list[i].add(new int[] { curr[0][0], curr[0][1] });
            list[i].add(new int[] { curr[1][0], curr[1][1] });
            list[i].add(new int[] { curr[2][0], curr[2][1] });
        }

        long ans = Long.MIN_VALUE;
        for (int i1 = 0; i1 < n; i1++) {
            for (int j1 = 0; j1 < 3; j1++) {
                int[] c1 = list[i1].get(j1);

                for (int i2 = 0; i2 < n; i2++) {
                    if (i2 == i1) {
                        continue;
                    }
                    for (int j2 = 0; j2 < 3; j2++) {
                        int[] c2 = list[i2].get(j2);
                        if (c1[0] == c2[0]) {
                            continue;
                        }

                        for (int i3 = 0; i3 < n; i3++) {
                            if (i3 == i1 || i3 == i2) {
                                continue;
                            }
                            for (int j3 = 0; j3 < 3; j3++) {
                                int[] c3 = list[i3].get(j3);

                                if (c1[0] == c3[0] || c2[0] == c3[0]) {
                                    continue;
                                }
                                ans = Math.max(ans, (long) (c1[1]) + c2[1] + c3[1]);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
}
