class Solution {
public:
    using LL = long long;

    long long maximumValueSum(vector<vector<int>>& A) 
    {
        vector<pair<LL, pair<int, int>>> vals;

        int H = A.size();
        int W = A[0].size();

        for(int i = 0; i < H; i++)
        {
            for(int j = 0; j < W; j++)
            {
                vals.push_back({A[i][j], {i, j}});
            }
        }                
        LL res = -1e18;

        sort(vals.rbegin(), vals.rend());

        for(int i = 0; i < vals.size(); i++)
        {
            LL v1 = vals[i].first;
            int y1 = vals[i].second.first;
            int x1 = vals[i].second.second;            

            for(int j = i+1; j < vals.size(); j++)
            {
                LL v2 = vals[j].first;
                int y2 = vals[j].second.first;
                int x2 = vals[j].second.second;            

                if(y2 == y1 || x2 == x1) continue;

                for(int k = j+1; k < vals.size(); k++)
                {
                    LL v3 = vals[k].first;
                    int y3 = vals[k].second.first;
                    int x3 = vals[k].second.second;            

                    if(y3 == y1 || y3 == y2 || x3 == x1 || x3 == x2) continue;

                    res = max(res, v1 + v2 + v3);
                    break;
                }
            }
        }
        return res;





        for(int i = 0; i < H; i++)
        {
            for(int j = 0; j < W; j++)
            {
                long long sum = A[i][j];

                unordered_set<int> Y = { i };
                unordered_set<int> X = { j };

                for(int k = vals.size()-1; k >= 0 && Y.size() < 3; k--)
                {
                    auto val = vals[k].first;
                    auto y = vals[k].second.first;
                    auto x = vals[k].second.second;

                    if(Y.count(y) || X.count(x)) continue;

                    Y.insert(y);
                    X.insert(x);
                    sum += val;                                        
                }
                if(Y.size() == 3)
                {
                    res = max(res, sum);
                }
            }
        }        
        return res;
    }
};

