#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int n=b.size();
        int m=b[0].size();
        vector<vector<pair<int,int>>> v(m);
        for(int j=0;j<m;j++){
            vector<vector<int>> a;
            for(int i=0;i<n;i++){
                vector<int> c;
                c.push_back(b[i][j]);
                c.push_back(i);
                c.push_back(j);
                a.push_back(c);
            }
            sort(a.begin(),a.end());
            for(int i=n-3;i<n;i++){
                int x=a[i][1];
                int y=a[i][2];
                v[j].push_back({x,y});
            }
        }
        // for(int i=0;i<m;i++){
            // for(auto it:v[0]){
            //     cout<<it.first<<" "<<it.second<<"\n";
            // }
        // }
        ll ans=-1e18;
        for(int i=0;i<m;i++){
            for(auto it1:v[i]){
                int x1=it1.first;
                int y1=it1.second;
                // cout<<x1<<" "<<y1<<"\n";
                for(int j=0;j<m;j++){
                    if(j==i) continue;
                    for(auto it2:v[j]){
                        int x2=it2.first;
                        int y2=it2.second;
                        if(x1!=x2 && y1!=y2){
                            for(int k=0;k<m;k++){
                                if(k==i || k==j) continue;
                                for(auto it3:v[k]){
                                    int x3=it3.first;
                                    int y3=it3.second;
                                    if(x3!=x1 && x3!=x2 && y3!=y2 && y3!=y1){
                                        ll val=(ll)b[x1][y1]+(ll)b[x2][y2]+(ll)b[x3][y3];
                                        // cout<<b[x1][y1]<<" "<<b[x2][y2]<<" "<<b[x3][y3]<<"\n";
                                        ans=max(ans,val);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
