class Solution {
public:
    static bool cmp(pair<int,int>&p1,pair<int,int>&p2){
        if(p1.first==p2.first)return p1.second>p2.second;
        return p1.second>p2.second;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(),m=board[0].size();
        long long ans=LLONG_MIN;
        vector<vector<int>>maxElement(m,vector<int>(3,0));
        for(int i=0;i<m;i++){
            vector<pair<int,int>>temp;
            for(int j=0;j<n;j++)temp.push_back({board[j][i],j});
            sort(temp.rbegin(),temp.rend());
            for(int j=0;j<3;j++)maxElement[i][j]=temp[j].second;
        }
        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                for(int k=j+1;k<m;k++){
                    long long curr=0;
                     for(int l1=0;l1<3;l1++){
                         for(int l2=0;l2<3;l2++){
                             for(int l3=0;l3<3;l3++){
                                 if(maxElement[i][l1]==maxElement[j][l2]||maxElement[j][l2]==maxElement[k][l3]||maxElement[i][l1]==maxElement[k][l3])continue;
                                 long long curr=0;
                                 curr+=board[maxElement[i][l1]][i];
                                 curr+=board[maxElement[j][l2]][j];
                                 curr+=board[maxElement[k][l3]][k];
                                 ans=max(ans,curr);
                             }
                         }
                     }
                }
            }
        }
        return ans;
    }
};
