    def maximumValueSum(self, board: List[List[int]]) -> int:
        m,n = len(board), len(board[0])
        data = []
        for r in board:
            h = []
            for i,v in enumerate(r):
                if len(h)==3: heappushpop(h, (v,i))
                else: heappush(h, (v,i))
            data.append(h)
        res = -math.inf
        for a,b,c in combinations(data, 3):
            for x,y,z in product(a,b,c):
                if x[1]!=y[1] and x[1]!=z[1] and y[1]!=z[1]:
                    res = max(res, x[0]+y[0]+z[0])
        return res
