class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<pair<long long, vector<long long>>> arr = {};
        for (long long i = 0; i < board.size(); i++) {
            for (long long j = 0; j < board[0].size(); j++) {
                arr.push_back({board[i][j], {i, j}});
            }
        }
        long long answer = board[0][0];
        answer += board[1][1];
        answer += board[2][2];
        sort(arr.begin(), arr.end(), greater<pair<long long, vector<long long>>>());
        for (long long i = 0; i < arr.size(); i++) {
            long long iScore = arr[i].first;
            long long iRow = arr[i].second[0];
            long long iCol = arr[i].second[1];
            for (long long j = i+1; j < arr.size(); j++) {
                long long jScore = iScore + arr[j].first;
                long long jRow = arr[j].second[0];
                long long jCol = arr[j].second[1];
                if (iRow == jRow) continue;
                if (iCol == jCol) continue;
                for (long long k = j+1; k < arr.size(); k++) {
                    long long kScore = jScore + arr[k].first;
                    if (kScore <= answer) break;
                    long long kRow = arr[k].second[0];
                    long long kCol = arr[k].second[1];
                    if (kRow == iRow) continue;
                    if (kCol == iCol) continue;
                    if (kRow == jRow) continue;
                    if (kCol == jCol) continue;
                    // cout << "answer" << endl;
                    // cout << "I " << iRow << ", " << iCol << endl;
                    // cout << "J " << jRow << ", " << jCol << endl;
                    // cout << "K " << kRow << ", " << kCol << endl;
                    answer = kScore;
                }
            }
        }
        return answer;
    }
};
