class Solution {
public:
    vector<vector<int>> row_maxes; 
    long long eval(int first,int second,int third,vector<vector<int>>& board){
        long long one = 1;
        long long sum = -(one<<40);
        for(int i = 0;i<3;i++){
            int it = row_maxes[first][i]; 
            for(int j = 0;j<3;j++){
                int jt = row_maxes[second][j];
                if(jt == it){
                    continue;
                };
                for(int k = 0;k<3;k++){
                    int kt = row_maxes[third][k];
                    if(kt==it || kt==jt) continue;
                    long long temp = board[first][it];
                    temp += board[second][jt];
                    temp += board[third][kt];
                    sum = max(sum,temp);
                };
            };
        };
        return sum;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        //number to value conversion
        // number = i*n+j;
        // j = number%n;
        //i similarly
        int n = board.size();
        int m = board[0].size();
        row_maxes.resize(n);

        for(int i = 0;i<n;i++){
            set<pair<int,int>> pq;

            for(int j = 0;j<m;j++){
                if(pq.size()<3){
                    pq.insert({board[i][j],j});
                    continue;
                }
                auto ns = *(pq.begin());
                if(board[i][ns.second]<board[i][j]){
                    pq.erase(ns);
                    pq.insert({board[i][j],j});
                };
            };
            vector<int> temp;
            for(auto it: pq){
                temp.push_back(it.second);
            };
            row_maxes[i] = temp;
        };
        // cout<<"row maxes is \n";
        // for(auto it:row_maxes){
        //     for(auto jt : it){
        //         cout<<jt<<" ";
        //     };
        //     cout<<endl;
        // }
        long long one = 1;
        long long sum = -(one<<40);
        for(int i = 0;i<n;i++){
            for(int j = i+1;j<n;j++){
                for(int k = j+1;k<n;k++){
                    sum = max(sum,eval(i,j,k,board));
                }
            }
        };
        return sum;
    }
};
