class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {

        long long ans=-1e12;
        int n=a.size();
        int m=a[0].size();

        vector<vector<pair<int,int>>> b;

        for(int i=0; i<n; i++)
        {
            vector<pair<int,int>> tmp;
            for(int j=0; j<m; j++)
            {
                tmp.push_back({a[i][j],j});
            }

            sort(tmp.begin(),tmp.end());
            b.push_back(tmp);

        }


        for(int i1=0; i1<n; i1++)
        {
            for(int i2=i1+1; i2<n; i2++)
            {
                for(int i3=i2+1; i3<n; i3++)
                {
                    for(int j1=m-1; j1>=m-3; j1--)
                    {
                        for(int j2=m-1; j2>=m-3; j2--)
                        {
                            if(b[i2][j2].second==b[i1][j1].second) continue;

                            for(int j3=m-1; j3>=m-3; j3--)
                            {
                                if(b[i3][j3].second==b[i2][j2].second || b[i3][j3].second==b[i1][j1].second) continue;

                                long long tmp=0;
                                tmp+=0ll+b[i3][j3].first+b[i1][j1].first+b[i2][j2].first;
                                ans=max(ans,tmp);
                                // cout<<tmp<<" "<<i1<<" "<<i2<<" "<<i3<<" "<<
                                //     b[1][j1].second<<" "<<b[i2][j2].second<<" "<<b[i3][j3].second<<endl;
                                // cout<<b[i1][j1].first<<" "<<b[i2][j2].first<<" "<<b[i3][j3].first<<endl;
                            }
                        }
                    }
                }
            }
        }

        // for(int i=0; i<n; i++) cout<<b[i][m-1].first<<" ";
        cout<<endl;
        return ans;
    }
};
