type Value struct {
	val int
	idx int
}

func maximumValueSum(board [][]int) int64 {
	m, n := len(board), len(board[0])

    rowTop3 := make([][3]Value, m)
	colTop3 := make([][3]Value, n)

	for i := 0; i < m; i++ {
		rowTop3[i] = GetTop3Values(board[i])
	}

	for j := 0; j < n; j++ {
		col := make([]int, m)
		for i := 0; i < m; i++ {
			col[i] = board[i][j]
		}
		colTop3[j] = GetTop3Values(col)
	}

	var maxSum int = math.MinInt64

	for r1 := 0; r1 < m-2; r1++ {
		for r2 := r1 + 1; r2 < m-1; r2++ {
			for r3 := r2 + 1; r3 < m; r3++ {
				candidates := []int{}
				for i1 := 0; i1 < 3; i1++ {
					for i2 := 0; i2 < 3; i2++ {
						for i3 := 0; i3 < 3; i3++ {
							c1, c2, c3 := rowTop3[r1][i1].idx, rowTop3[r2][i2].idx, rowTop3[r3][i3].idx
							if c1 != c2 && c2 != c3 && c1 != c3 {
								sum := rowTop3[r1][i1].val + rowTop3[r2][i2].val + rowTop3[r3][i3].val
								candidates = append(candidates, sum)
							}
						}
					}
				}
				sort.Ints(candidates)
				if len(candidates) > 0 && candidates[len(candidates)-1] > maxSum {
					maxSum = candidates[len(candidates)-1]
				}
			}
		}
	}

	return int64(maxSum)
}


func GetTop3Values(arr []int) [3]Value {
	n := len(arr)
	top3 := [3]Value{
		{val: math.MinInt32, idx: -1},
		{val: math.MinInt32, idx: -1},
		{val: math.MinInt32, idx: -1},
	}

	for i := 0; i < n; i++ {
		if arr[i] > top3[0].val {
			top3[2] = top3[1]
			top3[1] = top3[0]
			top3[0] = Value{val: arr[i], idx: i}
		} else if arr[i] > top3[1].val {
			top3[2] = top3[1]
			top3[1] = Value{val: arr[i], idx: i}
		} else if arr[i] > top3[2].val {
			top3[2] = Value{val: arr[i], idx: i}
		}
	}
	return top3
}
