using namespace std;
using ll = long long;

class Solution {
public:
    ll maximumValueSum(vector<vector<int>>& a) {
		int n = a.size(), m = a[0].size();
		vector<vector<pair<int, int>>> dp(n, vector<pair<int, int>>(3));
		vector<vector<int>> idx(n, vector<int>(3, -1));
		for (int i = 0; i < n; ++i) {
			vector<pair<int, int>> b;
			for (int j = 0; j < m; ++j) {
				b.push_back({a[i][j], j});
			}
			sort(b.begin(), b.end(), greater<pair<int, int>>());
			for (int j = 0; j < 3; ++j) dp[i][j] = b[j];
		}
		ll ans = -3e9;
		for (int i = 0; i < n; ++i) {
			for (int j = i + 1; j < n; ++j) {
				for (int k = j + 1; k < n; ++k) {
					for (int x = 0; x < 3; ++x) {
						for (int y = 0; y < 3; ++y) {
							for (int z = 0; z < 3; ++z) {
								if (dp[i][x].second != dp[j][y].second && dp[i][x].second != dp[k][z].second && dp[j][y].second != dp[k][z].second) {
									ans = max(ans, (ll)dp[i][x].first + dp[j][y].first + dp[k][z].first);
								}
							}
						}
					}
				}
			}
		}
		return ans;
    }
};
