#include<iostream>
#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef double  dbl;
typedef float fl;
#define minPq  priority_queue <ll, vector<ll>, greater<ll>> 

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& nums) {
        ll ans=-1e18;
        ll n=nums.size();
        ll m=nums[0].size();
        vector<vector<pair<ll,ll>>>a(n);
        for(ll i=0;i<n;i++)
        {
            for(ll j=0;j<m;j++)
            {
                a[i].push_back({nums[i][j],j});
            }
        }
        for(int i=0;i<n;i++)
        {
            sort(a[i].begin(),a[i].end(),greater<pair<ll,ll>>());
        }
        for(int i=0;i<n;i++)
        {
            for(ll j=i+1;j<n;j++)
            {
                for(ll k=j+1;k<n;k++)
                {   
                    for(ll z=0;z<m;z++)//first row ka kaun sa element
                    {
                        for(ll aa=0;aa<m;aa++)//j ka max 
                        {
                            if(a[i][z].second!=a[j][aa].second)
                            {
                                for(ll bb=0;bb<m;bb++)//k ka max
                                {
                                    if(a[k][bb].second!=a[i][z].second && a[k][bb].second!=a[j][aa].second)
                                    {
                                        ans=max(ans,a[i][z].first+a[j][aa].first+a[k][bb].first);
                                        break;
                                    }
                                }
                                break;
                            }
                        }
                         for(ll aa=0;aa<m;aa++)//k ka max 
                        {
                            if(a[i][z].second!=a[k][aa].second)
                            {
                                for(ll bb=0;bb<m;bb++)//j ka max
                                {
                                    if(a[j][bb].second!=a[i][z].second && a[j][bb].second!=a[k][aa].second)
                                    {
                                        ans=max(ans,a[i][z].first+a[k][aa].first+a[j][bb].first);
                                        break;
                                    }
                                }
                                break;
                            }
                        }

                    }
                }
            }
        }
        return ans;
    }
};

