class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        int siz=n*m;
        vector<pair<int,pair<int,int>>> v;
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                v.push_back({board[i][j],{i,j}});
            }
        }
        sort(v.begin(),v.end());
        // for(auto p: v){
        //     cout<<p.first<<" : "<<p.second.first<<" , "<<p.second.second<<"\n";
        // }
        long long ans=LLONG_MIN,curscore,tot,second_tot,third_tot,temp;
        for(int i=v.size()-1; i>=0; i--){
            if(i>1){
                if((1LL*v[i].first+1LL*v[i-1].first+1LL*v[i-2].first) <= ans) break;
            }
            vector<pair<int,int>> t;
            t.push_back(v[i].second);
            tot=(1LL*v[i].first);
            for(int j=i-1; j>=0; j--){
                if(j==0){
                    temp=v[j].first;
                }else{
                    temp=v[j].first+v[j-1].first;
                }
                if((tot+1LL*temp) < ans){
                    break;
                }
                bool valid=true;
                for(auto& p: t){
                    if(p.first==v[j].second.first || p.second == v[j].second.second){
                        valid=false;
                        break;
                    }
                }
                if(valid){
                    t.push_back(v[j].second);
                    second_tot = tot + (1LL*v[j].first);
                    for(int k=j-1; k>=0; k--){
                        third_tot = second_tot + (1LL*v[k].first);
                        if(third_tot <= ans) break;
                         bool valid=true;
                        for(auto& p: t){
                            if(p.first==v[k].second.first || p.second == v[k].second.second){
                                valid=false;
                                break;
                            }
                        }
                        if(valid){
                            ans=max(ans,third_tot);
                            break;
                        }
                    }
                    t.pop_back();
                }
            }
            // if(tot==135){
            //       for(auto p: t){
            //     cout<<p.first<<" - "<<p.second<<"\n";
            // }

        }
            // cout<<"taken :"<<taken<<"\n";
            // for(auto p: t){
            //     cout<<p.first<<" - "<<p.second<<"\n";
            // }
        return ans;
    }
};
