class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        a = []
        for row in board:
            a.append(sorted([(x, i) for i, x in enumerate(row)])[-3:])
        # print(a)
        st = []
        ans = -inf
        def dfs(i: int, s: int) -> None:
            if len(st) == 3:
                nonlocal ans
                ans = max(ans, s)
                return
            if i == m:
                return

            dfs(i + 1, s)
            for x, idx in a[i]:
                if idx not in st:
                    st.append(idx)
                    dfs(i + 1, s + x)
                    st.pop()

        dfs(0, 0)
        return ans
