class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int rows = board.size();
        int cols = board[0].size();
        long long maxSum = LLONG_MIN;

        // Store the top 3 maximum values and their corresponding column indices for each row
        vector<vector<long long>> topValues(rows, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> topColumns(rows, vector<int>(3, -1));

        // Find the top 3 values for each row and their column indices
        for (int r = 0; r < rows; ++r) {
            vector<pair<long long, int>> colValues;
            for (int c = 0; c < cols; ++c) {
                colValues.emplace_back(board[r][c], c);
            }
            sort(colValues.rbegin(), colValues.rend()); // Sort in descending order

            // Store the top 3 values and their columns
            for (int k = 0; k < 3 && k < cols; ++k) {
                topValues[r][k] = colValues[k].first;
                topColumns[r][k] = colValues[k].second;
            }
        }

        // Try all combinations of 3 different rows
        for (int row1 = 0; row1 < rows; ++row1) {
            for (int row2 = row1 + 1; row2 < rows; ++row2) {
                for (int row3 = row2 + 1; row3 < rows; ++row3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (topColumns[row2][j] == topColumns[row1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (topColumns[row3][k] == topColumns[row1][i] || 
                                    topColumns[row3][k] == topColumns[row2][j]) continue;

                                long long currentSum = topValues[row1][i] + topValues[row2][j] + topValues[row3][k];
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
