#define ll long long
class Solution {
public:
    int n,m;
    ll dp[102][102];
    vector<vector<ll>> smax;
    ll solve(int i,int x,vector<vector<int>> &board){
        if(i==n)
            return -1e10;
        if(dp[i][x]!=-1)
            return dp[i][x];
        ll sum = -1e12;
        for(int j = 0;j<m;j++){
            if(!x)
                sum = max(sum,board[i][j]+solve(i+1,j+1,board));
            else if(x-1!=j){
                ll mx = INT_MIN;

                    for(int v = 0;v<m;v++){
                        if(v!=x-1 && v!=j)
                            mx = max(mx,(1LL)*smax[i+1][v]);
                    }
                sum = max(sum,board[i][j]+mx);
            }
        }
        sum = max(sum,solve(i+1,x,board));
        return dp[i][x] = sum;

    }
    long long maximumValueSum(vector<vector<int>>& board) {
        n = board.size(),m = board[0].size();
        memset(dp,-1,sizeof(dp));
        smax.resize(n+1,vector<ll> (m+1,INT_MIN));
        for(int j = 0;j<m;j++){
            for(int i = n-1;i>=0;i--){
                if(i==n-1)
                    smax[i][j] = board[i][j];
                else
                    smax[i][j] = max(smax[i+1][j],(1LL)*board[i][j]);
            }
        }
        return solve(0,0,board);
    }
};
