class Solution {
public:
    #define ll long long int
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        ll res = -1e10;
        vector<vector<pair<ll, int>>> cells(m, vector<pair<ll, int>>());
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                cells[i].push_back({board[i][j], j});
            }
            sort(cells[i].begin(), cells[i].end(), cmp);
        }
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < m; j++) {
                if(i == j) continue;
                for(int k = 0; k < m; k++) {
                    if(k == j || k == i) continue;
                    for(int a = 0; a < 3; a++) {
                        for(int b = 0; b < 3; b++) {
                            if(cells[i][a].second == cells[j][b].second) continue;
                            for(int c = 0; c < 3; c++) {
                                if(cells[k][c].second == cells[i][a].second || cells[k][c].second == cells[j][b].second) {
                                    continue;
                                }
                                res = max(res, cells[i][a].first + cells[j][b].first + cells[k][c].first);
                            }
                        }
                    }
                }
            }
        }
        return res;
    }

    static bool cmp(pair<ll, int>& a, pair<ll, int>& b) {
        if(a.first == b.first) return a.second < b.second;
        return a.first > b.first;
    }

};
