class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size() , n = board[0].size();

        vector<pair<int,pair<int,int>>> vp;
        for(int i=0;i<m;i++){
            vector<pair<int,int>> temp;
            for(int j=0;j<n;j++){
                temp.push_back({board[i][j] , j});
            }
            sort(temp.begin() , temp.end());
            reverse(temp.begin() , temp.end());
            for(int j=0;j<3;j++){
                vp.push_back({temp[j].first , {i , temp[j].second }});
            }
        }

        // for(int i=0;i<vp.size();i++){
        //     cout<<vp[i].first<<" ";
        //     cout<<vp[i].second.first<<" "<<vp[i].second.second<<" \n";
        // }

        long long res  = -1e12;

        for(int i=0;i<vp.size();i++){
            long long ans = vp[i].first;
            int idx1 = vp[i].second.first , idx2 = vp[i].second.second;
            for(int j=0;j<vp.size();j++){
                if(vp[j].second.first != idx1 && vp[j].second.second != idx2){
                    long long ansHere = ans + vp[j].first;
                    for(int k=0;k<vp.size();k++){
                        if(vp[k].second.first != idx1 && vp[k].second.second != idx2
                            && vp[k].second.first != vp[j].second.first &&
                            vp[k].second.second != vp[j].second.second
                            ){
                            res = max(res , ansHere + vp[k].first); 
                        }
                    }
                }
            }
        }
        return res;
    }
};
