class Solution {
public:
    long long dp[4][101][102][102];
    long long solve(int i,int p1,int p2,int c,int n,int m,vector<vector<int>>&board,vector<vector<pair<int,int>>>&v){
        if(i>=n){
            if(c==3)return 0;
            return -1e18;
        }

        if(dp[c][i][p1+1][p2+1]!=-1)return dp[c][i][p1+1][p2+1];
        long long ans=-1e18;
        if(c<3){

                if(c==0){
                      int maxi=INT_MIN;
            for(int k=0;k<3;k++){
                 maxi=v[i][k].first;
                    ans=max(ans,maxi+solve(i+1,v[i][k].second,p2,c+1,n,m,board,v));
            }
                 // ans=max(ans,maxi+solve(i+1,p1,p2,c+1,n,m,board,v));
                    // ans=max(ans,board[i][j]+solve(i+1,j,p2,c+1,n,m,board,v));
                }else if(c==1){
                      int maxi=INT_MIN; int k1=0;
            for(int k=0;k<3;k++){
                if(v[i][k].second!=p1){
                    maxi=v[i][k].first;
                    ans=max(ans,maxi+solve(i+1,p1,v[i][k].second,c+1,n,m,board,v));
                }
            }


                }
                else if(c==2){
            int maxi=INT_MIN;
            for(int k=0;k<3;k++){
                if(v[i][k].second!=p1&&v[i][k].second!=p2){
                     maxi=v[i][k].first;
                     ans=max(ans,maxi+solve(i+1,p1,p2,c+1,n,m,board,v));
                }
            }


                }

        }
        ans=max(ans,solve(i+1,p1,p2,c,n,m,board,v));
        return dp[c][i][p1+1][p2+1]=ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size(); int m=board[0].size();
        memset(dp,-1,sizeof(dp));
           vector<vector<pair<int,int>>> v(n);


         for(int j=0;j<n;j++){
             vector<pair<int,int>> x;
             for(int i=0;i<m;i++){
                 x.push_back({board[j][i],i});
             }
             sort(x.rbegin(),x.rend());
             vector<pair<int,int>> p;
             for(int i=0;i<3;i++){
                 p.push_back({x[i].first,x[i].second});
             }
             v[j]=p;
         }
      return solve(0,-1,-1,0,n,m,board,v);
    }
};
