using ll = long long;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        ll inf = 1e15;

        ll n = b.size();
        ll m = b[0].size();

        auto board = vector(n,vector(m,0ll));
        for(ll i=0; i<n; i++) {
            for(ll j=0; j<m; j++) board[i][j] = j;
        }

        for(ll i=0; i<n; i++) {
            sort(board[i].begin(),board[i].end(),[&](ll x,ll y){
                return b[i][x] > b[i][y];
            });
        }

        ll ans = -inf;

        for(ll i=0; i<n; i++) {
            for(ll j=i+1; j<n; j++) {
                for(ll k=j+1; k<n; k++) {

                    // if((i!=0 || j!=1 || k!=3)) continue;
                    // cout<<"rnm: "<<i<<" "<<j<<" "<<k<<endl;

                    for(ll ii=0; ii<3; ii++) {
                        for(ll jj=0; jj<3; jj++) {
                            for(ll kk=0; kk<3; kk++) {
                                if(board[i][ii]==board[j][jj] || board[j][jj]==board[k][kk] || board[i][ii]==board[k][kk]) continue;
                                // cout<<ii<<" "<<board[i][ii]<<" "<<b[i][board[i][ii]]<<endl;
                                // cout<<jj<<" "<<board[j][jj]<<" "<<b[j][board[j][jj]]<<endl;
                                // cout<<kk<<" "<<board[k][kk]<<" "<<b[k][board[k][kk]]<<endl;
                                // cout<<endl;
                                ans = max(ans,ll(b[i][board[i][ii]])+ll(b[j][board[j][jj]])+ll(b[k][board[k][kk]]));
                            }
                        }
                    }

                }
            }
        }

        return ans;
    }
};
